// Single Line Comment.
/*
Multi-Line Comments.
*/
console.log("Welcome to TypeScript!");
var x = 100;
var y = 200;
var sum = x + y;
console.log("Sum of " + x + " and " + y + " is " + sum + ".");
// Use backquote(`) instead of single-quote(').
console.log("Addition of ".concat(x, " and ").concat(y, " is ").concat(sum, "."));
// TypeScript Data Types.
// String
var s_name = "Harshad";
// To explicitly specify a variable as String.
var course = "TypeScript Fundamentals";
console.log("Welcome ".concat(s_name, " to session.\nCourse selected: ").concat(course, "."));
// Number
var fees = 4500000;
console.log("Fees: ".concat(fees, "."));
// Any
var my_Var = "Vishal";
console.log("Faculty: ".concat(my_Var, "."));
my_Var = 101;
console.log("Faculty: ".concat(my_Var, "."));
// Boolean
var permanent_Status = true;
console.log("Status: ".concat(permanent_Status, "."));
